<div class="col-xs-6 col-sm-4 col-md-3">    <div class="item">        <a class="link" target="_blank" href="<?php  echo suiranx_nav_from_url($article->ID);  ?>" <?php if ($zbp->Config('suiranx_nav')->nofollow_switch) { ?>rel="nofollow"<?php } ?>><i class="autoleft fa fa-angle-right" title="直达网站"></i></a>        <a class="a" href="<?php  echo $article->Url;  ?>" title="<?php  echo $article->Title;  ?>" target="_blank">            <img src="<?php if (strlen ( $article->Metas->coverimg ) > 4) { ?><?php  echo $article->Metas->coverimg;  ?><?php }else{  ?><?php  echo suiranx_nav_firstimg2($article);  ?><?php } ?>" alt="<?php  echo $article->Title;  ?>" title="<?php  echo $article->Title;  ?>">            <h3><span class="top-show">[顶]</span><?php  echo $article->Title;  ?></h3>            <p>            <?php if (strlen($article->Intro)>0) { ?>				<?php $intro= trim(SubStrUTF8(TransferHTML($article->Intro,'[nohtml]'),100)).'...'; ?>				<?php }else{  ?>				<?php $intro= trim(SubStrUTF8(TransferHTML($article->Content,'[nohtml]'),100)).'...'; ?>			<?php } ?>			<?php  echo $intro;  ?></p>        </a>    </div></div>